{
  "metric_id": "{{ _.sample_metric_id }}",
  "name": "Login Success Rate (Updated)",
  "short_name": "login_success_rate",
  "description": "Updated: Percentage of successful login attempts",
  "category": "Authentication",
  "tier": "Tier-1",
  "business_domain": "Authentication",
  "metric_type": "operational",
  "tags": ["security", "authentication", "user-experience", "updated"],
  "alignment": {
    "objectives": ["Improve user experience"],
    "kpis": ["User satisfaction"],
    "stakeholders": ["Product Team", "Security Team"]
  },
  "definition": {
    "calculation": "(successful_logins / total_login_attempts) * 100",
    "unit": "percentage",
    "format": "0.00%"
  },
  "data": {
    "sources": ["Auth Service Database"],
    "refresh_frequency": "Real-time",
    "latency": "< 1 minute"
  },
  "governance": {
    "owner": "auth-team@example.com",
    "steward": "data-team@example.com",
    "approvers": ["security-lead@example.com"]
  },
  "dimensions": [
    {
      "name": "Region",
      "values": ["US", "EU", "APAC"]
    },
    {
      "name": "Auth Method",
      "values": ["Password", "OAuth", "SSO"]
    }
  ],
  "targets_and_alerts": {
    "targets": [
      {
        "type": "threshold",
        "value": 95,
        "operator": ">="
      }
    ],
    "alerts": [
      {
        "condition": "value < 90",
        "severity": "high",
        "recipients": ["oncall@example.com"]
      }
    ]
  },
  "visualization": {
    "chart_type": "line",
    "default_view": "Last 24 hours"
  },
  "relationships": {
    "depends_on": ["METRIC-TOTAL-LOGIN-ATTEMPTS"],
    "influences": ["METRIC-USER-SATISFACTION"]
  },
  "operational_usage": {
    "dashboards": ["Auth Dashboard", "Executive Dashboard"],
    "reports": ["Weekly Auth Report"],
    "automations": ["Auto-scaling trigger"]
  },
  "metadata": {
    "created_date": "2025-01-01T00:00:00Z",
    "last_updated": "2025-11-15T00:00:00Z",
    "version": "2.0",
    "status": "active"
  }
}

